using UnityEngine;using UnityEditor;namespace EditorWindowExtension.EditorGUIs {	public class ProgressBarWindow : EditorWindow {		float _float;		bool _start;		[MenuItem ("Tools/EditorGUI/ProgressBar Window")]		static void Open () {			EditorWindow window = GetWindow (typeof (ProgressBarWindow), false, "ProgressBar");			window.Show ();		}		void OnGUI () {			_float = Mathf.Max (_float, 0);			_float = Mathf.Min (_float, 100);			_float = EditorGUI.FloatField (new Rect(5, 5, 400, 17), "Precentage", _float);			EditorGUI.ProgressBar (new Rect (5, 27, 400, 17), _float / 100, "Progress");			if (GUI.Button(new Rect(5, 49, 400, 17), "Start")) {				_start = true;			}			if (GUI.Button (new Rect (5, 71, 400, 17), "Reset")) {				_float = 0f;				_start = false;			}		}		void Update () {			if (_start) {				_float += 0.1f;			}			if (_float >= 100f) {				_start = false;			}			Repaint ();		}	}}