using UnityEngine;using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (BoundsIntField))]	public class BoundsFieldIntInspector : Editor {		BoundsIntField _target;		void OnEnable () {			_target = (BoundsIntField)target;		}		public override void OnInspectorGUI () {			// First reload, no label, no extra options			_target.BoundsIntFirst = EditorGUILayout.BoundsIntField (_target.BoundsIntFirst);			// Second reload, with a label and a height of 200 pixels			_target.BoundsIntSecond = EditorGUILayout.BoundsIntField ("Bounds Int Second", _target.BoundsIntSecond, GUILayout.Height (200));			// Third reload, with a label, a tooltip and a size of 400 pixels * 100 pixels			var guiContent = new GUIContent ("Bounds Int Third", "This is the third int bounds.");			var guiLayoutOptions = new GUILayoutOption [] {				GUILayout.Width(400),				GUILayout.Height(100)			};			_target.BoundsIntThird = EditorGUILayout.BoundsIntField (guiContent, _target.BoundsIntThird, guiLayoutOptions);		}	}}