using UnityEngine;using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (EnumFlagsField))]	public class EnumFlagsFieldInspector : Editor {		EnumFlagsField _target;		void OnEnable () {			_target = (EnumFlagsField)target;		}		public override void OnInspectorGUI () {			// GUI style			GUIStyle style = new GUIStyle ();			style.fontSize = 30;			style.normal.background = Texture2D.whiteTexture;			style.normal.textColor = Color.red;			style.margin = new RectOffset (0, 0, 3, 3);			style.padding = new RectOffset (10, 10, 10, 10);			style.alignment = TextAnchor.MiddleLeft;			// default name flags			_target.Flags = (EnumFlagsField.EnumFlags)EditorGUILayout.EnumFlagsField ("Flags", _target.Flags);			// custom name flags			_target.CustomFlags = (EnumFlagsField.CustomEnumFlags)EditorGUILayout.EnumFlagsField (				"Custom Flags",				_target.CustomFlags,				style,				GUILayout.Height (50)			);		}	}}