using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (HelpBox))]	public class HelpBoxInspector : Editor {		public override void OnInspectorGUI () {			EditorGUILayout.HelpBox ("This is a simple help message.", MessageType.None);			EditorGUILayout.HelpBox ("This is an info message.", MessageType.Info, true);			EditorGUILayout.HelpBox ("This is an error message.", MessageType.Error, false);		}	}}