using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (InspectorTitleBar))]	public class InspectorTitleBarInspector : Editor {		bool _show;		InspectorTitleBar _target;		void OnEnable () {			_target = (InspectorTitleBar)target;		}		public override void OnInspectorGUI () {			_show = EditorGUILayout.InspectorTitlebar (_show, _target.transform);			if (_show) {				_target.transform.position = EditorGUILayout.Vector3Field ("Postion", _target.transform.position);				_target.transform.localScale = EditorGUILayout.Vector3Field ("Scale", _target.transform.localScale);			}		}	}}