using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (LabelField))]	public class LabelFieldInspector : Editor {		LabelField _target;		void OnEnable () {			_target = (LabelField)target;		}		public override void OnInspectorGUI () {			EditorGUILayout.LabelField ("This is the label of this label field.");			EditorGUILayout.LabelField (				"This is the label of this label field.",				"This is the content of this label field"			);			EditorGUILayout.PrefixLabel ("This is a prefix label field.");			_target.Value = EditorGUILayout.IntField (_target.Value);			EditorGUILayout.SelectableLabel ("This is a selectable label that you can copy / paste as you like.");		}	}}