using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (LayerTagField))]	public class LayerTagFieldInspector : Editor {		LayerTagField _target;		void OnEnable () {			_target = (LayerTagField)target;		}		public override void OnInspectorGUI () {			// layer field			_target.Layer = EditorGUILayout.LayerField ("Layer", _target.Layer);			// tag field			_target.Tag = EditorGUILayout.TagField ("Tag", _target.Tag);		}	}}