using UnityEngine;using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (ObjectField))]	public class ObjectFieldInspector : Editor {		ObjectField _target;		void OnEnable () {			_target = (ObjectField)target;		}		public override void OnInspectorGUI () {			// object field for Object			_target.Obj = EditorGUILayout.ObjectField ("Object", _target.Obj, typeof (Object), true);			// object field for Transform			_target.Transf = (Transform)EditorGUILayout.ObjectField ("Transform", (Object)_target.Transf, typeof (Transform), false);		}	}}