using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (PasswordField))]	public class PasswordFieldInspector : Editor {		PasswordField _target;		void OnEnable () {			_target = (PasswordField)target;		}		public override void OnInspectorGUI () {			// password, shown in *			_target.Password = EditorGUILayout.PasswordField("Password", _target.Password);			// actual content of the password			EditorGUILayout.LabelField ("Real Text", _target.Password);		}	}}