using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (Space))]	public class SpaceInspector : Editor {		public override void OnInspectorGUI () {			EditorGUILayout.LabelField ("Label", "This is a label.");			EditorGUILayout.LabelField ("Label", "There is a space following.");			EditorGUILayout.Space ();			EditorGUILayout.LabelField ("Label", "There is a space above.");			EditorGUILayout.LabelField ("Label", "This is a label.");		}	}}