using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (Toggle))]	public class ToggleInspector : Editor {		Toggle _target;		void OnEnable () {			_target = (Toggle)target;		}		public override void OnInspectorGUI () {			_target.ToggleRight = EditorGUILayout.Toggle ("Toggle Right", _target.ToggleRight);			_target.ToggleLeft = EditorGUILayout.ToggleLeft ("Toggle Left", _target.ToggleLeft);		}	}}