using UnityEditor;namespace InspectorExtension.EditorGUILayouts {	[CustomEditor (typeof (VectorField))]	public class VectorFieldInspector : Editor {		VectorField _target;		void OnEnable () {			_target = (VectorField)target;		}		public override void OnInspectorGUI () {			_target.Vec2 = EditorGUILayout.Vector2Field ("2D Vector", _target.Vec2);			_target.Vec2Int = EditorGUILayout.Vector2IntField ("2D Int Vector", _target.Vec2Int);			_target.Vec3 = EditorGUILayout.Vector3Field ("3D Vector", _target.Vec3);			_target.Vec3Int = EditorGUILayout.Vector3IntField ("3D Int Vector", _target.Vec3Int);			_target.Vec4 = EditorGUILayout.Vector4Field ("4D Vector", _target.Vec4);		}	}}