using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace InspectorExtension.HorizontalInspector {	[CustomEditor (typeof (Horizontal))]	public class HorizontalInspector : Editor {		public override void OnInspectorGUI () {			EditorGUILayout.BeginHorizontal ();			GUILayout.Button ("Button 1");					EditorGUILayout.BeginVertical ();			GUILayout.Button ("Button 2");			GUILayout.Button ("Button 3");			EditorGUILayout.EndVertical ();			GUILayout.Button ("Button 4");			EditorGUILayout.EndHorizontal ();			GUILayout.Button ("Button 5");		}	}}