using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace InspectorExtension.ScrollViewInspector {	[CustomEditor (typeof (ScrollView))]	public class ScrollViewInspector : Editor {		Vector2 _scroll;		string _text = "This is a string inside a scroll view.";		public override void OnInspectorGUI () {			EditorGUILayout.BeginHorizontal ();			_scroll = EditorGUILayout.BeginScrollView (				_scroll,				false, true,				GUILayout.ExpandWidth (true), GUILayout.Height (100)			);			EditorGUILayout.TextArea (_text);			EditorGUILayout.EndScrollView ();			if (GUILayout.Button ("Add More Text", GUILayout.Height (100))) {				_text += "\nAnd this is more text.";			}			EditorGUILayout.EndHorizontal ();			EditorGUILayout.Space ();		}	}}