using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace InspectorExtension.ToggleGroupInspector {	[CustomEditor (typeof (ToggleGroup))]	public class ToggleGroupInspector : Editor {		ToggleGroup _target;		void OnEnable () {			_target = (ToggleGroup)target;		}		public override void OnInspectorGUI () {			_target.Ax = EditorGUILayout.BeginToggleGroup ("Ax", _target.Ax);			_target.A [0] = EditorGUILayout.Toggle ("A1", _target.A [0]);			_target.A [1] = EditorGUILayout.Toggle ("A2", _target.A [1]);			_target.A [2] = EditorGUILayout.Toggle ("A3", _target.A [2]);			EditorGUILayout.EndToggleGroup ();			_target.Bx = EditorGUILayout.BeginToggleGroup ("Bx", _target.Bx);			_target.B [0] = EditorGUILayout.Toggle ("B1", _target.B [0]);			_target.B [1] = EditorGUILayout.Toggle ("B2", _target.B [1]);			_target.B [2] = EditorGUILayout.Toggle ("B3", _target.B [2]);			EditorGUILayout.EndToggleGroup ();		}	}}