/*** MenuBarExtension/Assets/Tools/MenuItems/Editor/NewLevelScene.cs*/using UnityEngine;using UnityEditor;using UnityEditor.SceneManagement;namespace MenuBarExtension.MenuItems {	public static class NewLevelScene {		[MenuItem ("Tools/Level/New Level Scene", false)]		private static void NewLevel () {			EditorSceneManager.SaveCurrentModifiedScenesIfUserWantsTo ();			EditorSceneManager.NewScene (NewSceneSetup.EmptyScene);		}		[MenuItem ("Tools/Level/Delete Selected Object #d", false)]		private static void DeleteSelected () {			Object.DestroyImmediate (Selection.activeGameObject);		}		[MenuItem ("Tools/Level/Delete Selected Object #d", true)]		private static bool ValidateDeleteSelected () {			return Selection.activeGameObject != null;		}	}}