using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUILayouts {	[CustomEditor (typeof (Box))]	public class BoxSceneView : Editor {		Box _target;		private void OnEnable () {			_target = (Box)target;		}		private void OnSceneGUI () {			Handles.BeginGUI ();			GUILayout.Box (new GUIContent("This is a box", _target.texture, "This is a box"));			Handles.EndGUI ();		}	}}