using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUILayouts {	[CustomEditor (typeof (Button))]	public class ButtonSceneView : Editor {		string _text;		private void OnSceneGUI () {			Handles.BeginGUI ();			if (GUILayout.Button ("Button")) {				_text = "Button clicked";			}			if (GUILayout.RepeatButton ("Repeat Button")) {				_text = "Repeat button held";			}			GUIStyle style = new GUIStyle ("label");			style.normal.textColor = Color.white;			GUILayout.Label (_text, style);			Handles.EndGUI ();		}	}}