using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUILayouts {	[CustomEditor (typeof (FlexibleSpace))]	public class FlexibleSpaceSceneView : Editor {		private void OnSceneGUI () {			Handles.BeginGUI ();			GUILayout.BeginHorizontal ();			GUILayout.Button ("Button 1");			GUILayout.FlexibleSpace ();			GUILayout.Button ("Button 2");			GUILayout.EndHorizontal ();			Handles.EndGUI ();		}	}}