using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUILayouts {	[CustomEditor (typeof (Scrollbar))]	public class ScrollbarSceneView : Editor {		float _hValue;		float _vValue;		private void OnSceneGUI () {			Handles.BeginGUI ();			_hValue = GUILayout.HorizontalScrollbar (_hValue, 1, 0, 10);			_vValue = GUILayout.VerticalScrollbar (_vValue, 1, 0, 10);			Handles.EndGUI ();		}	}}