using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUILayouts {	[CustomEditor (typeof (SelectionGrid))]	public class SelectionGridSceneView : Editor {		int _index;		int _col = 4, _row = 3;		List<GUIContent> contents;		private void OnEnable () {			contents = new List<GUIContent> ();			for (int i = 0; i < _row; i++) {				for (int j = 0; j < _col; j++) {					contents.Add (new GUIContent (string.Format ("Button {0}-{1}", i, j)));				}			}		}		private void OnSceneGUI () {			Handles.BeginGUI ();			_index = GUILayout.SelectionGrid (_index, contents.ToArray (), _col);			Handles.EndGUI ();		}	}}