using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUILayouts {	[CustomEditor (typeof (Toolbar))]	public class ToolbarSceneView : Editor {		int _index;		int _count = 5;		List<GUIContent> contents;		private void OnEnable () {			contents = new List<GUIContent> ();			for (int i = 0; i < _count; i++) {				contents.Add (new GUIContent (string.Format ("Button {0}", i)));			}		}		private void OnSceneGUI () {			Handles.BeginGUI ();			_index = GUILayout.Toolbar (_index, contents.ToArray (), "button", GUI.ToolbarButtonSize.FitToContents);			_index = GUILayout.Toolbar (_index, contents.ToArray (), "button", GUI.ToolbarButtonSize.Fixed);			Handles.EndGUI ();		}	}}