using System.Collections.Generic;using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUILayouts {	[CustomEditor (typeof (Window))]	public class WindowSceneView : Editor {		Rect _window = new Rect (5, 20, 200, 200);		private void OnSceneGUI () {			Handles.BeginGUI ();			_window = GUILayout.Window (0, _window, OnWindowGUI, "Window");			Handles.EndGUI ();		}		private void OnWindowGUI(int id) {			GUILayout.Button ("Button");			GUI.DragWindow ();		}	}}