using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUIs {	[CustomEditor (typeof (BringWindow))]	public class BringWindowSceneView : Editor {		float _xPos = 5, _yPos = 5;		Rect _window1 = new Rect (5, 25, 200, 100);		Rect _window2 = new Rect (45, 65, 200, 100);		Rect _window3 = new Rect (85, 105, 200, 100);		private void OnSceneGUI () {			Handles.BeginGUI ();			_window1 = GUI.Window (0, _window1, OnWindowGUI, "Window 1");			_window2 = GUI.Window (1, _window2, OnWindowGUI, "Window 2");			_window3 = GUI.Window (2, _window3, OnWindowGUI, "Window 3");			Handles.EndGUI ();		}		private void OnWindowGUI (int id) {			if (GUILayout.Button ("Bring next to front")) {				GUI.BringWindowToFront (id + 1 == 3 ? 0 : id + 1);			}			if (GUILayout.Button ("Bring to back")) {				GUI.BringWindowToBack (id);			}			GUI.DragWindow ();		}	}}