using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUIs {	[CustomEditor (typeof (DrawTextureWithTexCoords))]	public class DrawTextureWithTexCoordsSceneView : Editor {		DrawTextureWithTexCoords _target;		private void OnEnable () {			_target = (DrawTextureWithTexCoords)target;		}		private void OnSceneGUI () {			Handles.BeginGUI ();			GUI.DrawTextureWithTexCoords (new Rect (5, 5, 100, 100), _target._texture, new Rect (_target._x, _target._y, _target._width, _target._height));			GUI.DrawTexture (new Rect (110, 5, 200, 200), _target._texture);			GUI.DrawTexture (				new Rect (110 + 200 * _target._x, 5 + 200 - 200 * (_target._y + _target._height), 200 * _target._width, 200 * _target._height),				Texture2D.whiteTexture,				ScaleMode.StretchToFill,				true, 0,				new Color (255, 0, 0, 1),				1, 0);			Handles.EndGUI ();		}	}}