using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUIs {	[CustomEditor (typeof (FocusControl))]	public class FocusControlSceneView : Editor {		private void OnSceneGUI () {			Handles.BeginGUI ();			GUI.SetNextControlName ("Control 1");			GUILayout.TextField ("Control 1");			GUI.SetNextControlName ("Control 2");			GUILayout.TextField ("Control 2");			GUI.SetNextControlName ("Control 3");			GUILayout.TextField ("Control 3");			if (GUILayout.Button("Focus on control 1")) {				GUI.FocusControl ("Control 1");				Debug.Log (GUI.GetNameOfFocusedControl ());			}			if (GUILayout.Button ("Focus on control 2")) {				GUI.FocusControl ("Control 2");				Debug.Log (GUI.GetNameOfFocusedControl ());			}			if (GUILayout.Button ("Focus on control 3")) {				GUI.FocusControl ("Control 3");				Debug.Log (GUI.GetNameOfFocusedControl ());			}			Handles.EndGUI ();		}	}}