using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUIs {	[CustomEditor (typeof (ModalWindow))]	public class ModalWindowSceneView : Editor {		Rect _modalWindow = new Rect (5, 25, 200, 100);		Rect _commonWindow = new Rect (45, 65, 200, 100);		private void OnSceneGUI () {			Handles.BeginGUI ();			_modalWindow = GUI.ModalWindow (0, _modalWindow, OnModalWindowGUI, "Modal Window");			//_commonWindow = GUI.Window (1, _commonWindow, OnWindowGUI, "Common Window");			Handles.EndGUI ();		}		private void OnModalWindowGUI (int id) {			GUI.DragWindow ();		}		private void OnWindowGUI (int id) {			GUI.DragWindow ();		}	}}