using UnityEngine;using UnityEditor;namespace SceneViewExtension.GUIs {	[CustomEditor (typeof (ScrollTo))]	public class ScrollToSceneView : Editor {		public Vector2 scrollPos = Vector2.zero;		private void OnSceneGUI () {			Handles.BeginGUI ();			scrollPos = GUI.BeginScrollView (new Rect (10, 10, 200, 50), scrollPos, new Rect (0, 0, 300, 20));			if (GUI.Button (new Rect (0, 0, 150, 20), "Go Right"))				GUI.ScrollTo (new Rect (100, 0, 200, 20));			if (GUI.Button (new Rect (150, 0, 150, 20), "Go Left"))				GUI.ScrollTo (new Rect (0, 0, 200, 20));			GUI.EndScrollView ();			Handles.EndGUI ();		}	}}